def calculate_probability(favorable_outcomes, total_outcomes, output_fraction=False):
    from sympy import Rational
    if total_outcomes <= 0:
        return 'Error: Total outcomes must be greater than zero.'
    if favorable_outcomes < 0:
        return 'Error: Favorable outcomes cannot be negative.'
    result = Rational(favorable_outcomes, total_outcomes)
    return result if output_fraction else float(result)

