from sympy import sqrt

def calculate_distance_between_points(x1, y1, x2, y2):
    return round(float(sqrt((x2 - x1)**2 + (y2 - y1)**2)), 1)

from sympy import sqrt

def calculate_ac_length(a_x, a_y, c_x, c_y):
    return round(float(sqrt((c_x - a_x)**2 + (c_y - a_y)**2)), 1)

def calculate_angle_value(angle_A, angle_B, angle_C, angle_D, angle_E):
    angles = [angle_A, angle_B, angle_C, angle_D, angle_E]
    return max(angles)

def calculate_pentagon_angle(A, B, C):
    D = C
    E = 2 * C + 15
    angles = [A, B, C, D, E]
    return max(angles)

def calculate_angle_from_expressions(angle_A, angle_B, angle_C, angle_suffix):
    angle_D = angle_C  
    angle_E = 2 * angle_C + angle_suffix
    angles = [angle_A, angle_B, angle_C, angle_D, angle_E]
    return max(angles)
