evaluate_trig_functions.code:
from sympy import sin, cos, tan, deg

def evaluate_trig_functions(angle_degrees, function):
    angle_radians = deg(angle_degrees)
    if function == 'sin':
        return sin(angle_radians)
    elif function == 'cos':
        return cos(angle_radians)
    elif function == 'tan':
        return tan(angle_radians)
    else:
        raise ValueError('Invalid function specified. Use sin, cos, or tan.')

