from sympy import asin

def compute_arcsin(value):
    return asin(value)

from sympy import tan, acos

def evaluate_tan_arccos(x):
    if -1 <= x <= 1:
        return tan(acos(x))
    else:
        raise ValueError('Input must be in the range [-1, 1] for arccos.')

from sympy import sympify

def evaluate_expression(expression):
    try:
        result = sympify(expression)
        if result.is_number:
            result = float(result)
        return result
    except Exception as e:
        return str(e)
