from sympy import asin

def calculate_arcsin(value):
    return asin(value)

from sympy import acos, deg

def calculate_angle(a, b, c, degrees=False):
    A = acos((b**2 + c**2 - a**2) / (2 * b * c))
    B = acos((a**2 + c**2 - b**2) / (2 * a * c))
    C = acos((a**2 + b**2 - c**2) / (2 * a * b))
    if degrees:
        return deg(A), deg(B), deg(C)
    return A, B, C

from sympy import acos, deg

def compute_angles_from_sides(side_a, side_b, side_c, degrees=False):
    A = acos((side_b**2 + side_c**2 - side_a**2) / (2 * side_b * side_c))
    B = acos((side_a**2 + side_c**2 - side_b**2) / (2 * side_a * side_c))
    C = acos((side_a**2 + side_b**2 - side_c**2) / (2 * side_a * side_b))
    if degrees:
        return deg(A), deg(B), deg(C)
    return A, B, C
