from sympy import asin, pi

def evaluate_arcsin(value):
    if value < -1 or value > 1:
        raise ValueError('Value must be in the range [-1, 1].')
    return asin(value).evalf()

from sympy import sin, cos, tan, rad

def calculate_trig_functions(angle_degrees):
    angle_radians = rad(angle_degrees)
    return sin(angle_radians), cos(angle_radians), tan(angle_radians)
