from sympy import asin, pi

def evaluate_arcsin(value):
    try:
        value = float(value)
        if value < -1 or value > 1:
            return "Value out of range for arcsin"
        return asin(value)  # Will return in radians
    except ValueError:
        return "Invalid input"

