import sympy as sp

def sum_trigonometric_expression(start, end, function_type):
    total_sum = 0
    for angle in range(start, end + 1):
        if function_type == 'cos':
            total_sum += sp.cos(sp.rad(angle))
        elif function_type == 'sin':
            total_sum += sp.sin(sp.rad(angle))
        elif function_type == 'tan':
            total_sum += sp.tan(sp.rad(angle))
    return total_sum

