import sympy as sp
def calculate_triangle_angles(side_a, side_b, side_c):
    angle_A = sp.acos((side_b**2 + side_c**2 - side_a**2) / (2 * side_b * side_c))
    angle_B = sp.acos((side_a**2 + side_c**2 - side_b**2) / (2 * side_a * side_c))
    angle_C = sp.acos((side_a**2 + side_b**2 - side_c**2) / (2 * side_a * side_b))
    return sp.deg(angle_A), sp.deg(angle_B), sp.deg(angle_C)

from sympy import sympify, SympifyError
def evaluate_expression(expression):
    try:
        result = sympify(expression)
        if result.is_number:
            result = float(result)
        else:
            result = str(result)
        return result
    except SympifyError as e:
        return str(e)
