from sympy import sympify, SympifyError
def evaluate_expression(expression):
    try:
        result = sympify(expression)
        return result.evalf() if result.is_number else str(result)
    except SympifyError as e:
        return str(e)

import sympy as sp
def calculate_angles_of_triangle(a, b, c):
    if a + b > c and a + c > b and b + c > a:  # Check if a triangle can be formed
        angle_A = sp.acos((b**2 + c**2 - a**2) / (2 * b * c))
        angle_B = sp.acos((a**2 + c**2 - b**2) / (2 * a * c))
        angle_C = sp.acos((a**2 + b**2 - c**2) / (2 * a * b))
        return sp.deg(angle_A), sp.deg(angle_B), sp.deg(angle_C)
    else:
        return None, None, None

import sympy as sp
def calculate_triangle_angles(side_a, side_b, side_c):
    A = sp.acos((side_b**2 + side_c**2 - side_a**2) / (2 * side_b * side_c))
    B = sp.acos((side_a**2 + side_c**2 - side_b**2) / (2 * side_a * side_c))
    C = sp.acos((side_a**2 + side_b**2 - side_c**2) / (2 * side_a * side_b))
    return sp.deg(A), sp.deg(B), sp.deg(C)

from sympy import asin, pi

def evaluate_arcsin(value):
    try:
        value = float(value)
        if value < -1 or value > 1:
            return "Value out of range for arcsin"
        return asin(value)  # Will return in radians
    except ValueError:
        return "Invalid input"
