from sympy import acos, tan

def calculate_tan_arccos(x):
    if x < -1 or x > 1:
        return 'Invalid input'
    value = tan(acos(x**2))
    return value

from sympy import acos, deg

def calculate_angles_of_triangle_v2(a, b, c):
    if a + b <= c or a + c <= b or b + c <= a:
        return 'Invalid triangle dimensions'
    A = deg(acos((b**2 + c**2 - a**2) / (2 * b * c)))
    B = deg(acos((a**2 + c**2 - b**2) / (2 * a * c)))
    C = deg(acos((a**2 + b**2 - c**2) / (2 * a * b)))
    return (A, B, C)

from sympy import acos, deg, sqrt

def calculate_angles_of_triangle(a, b, c):
    if a + b <= c or a + c <= b or b + c <= a:
        return 'Invalid triangle dimensions'
    # Calculate angles using the Law of Cosines
    A = deg(acos((b**2 + c**2 - a**2) / (2 * b * c)))
    B = deg(acos((a**2 + c**2 - b**2) / (2 * a * c)))
    C = deg(acos((a**2 + b**2 - c**2) / (2 * a * b)))
    return (A, B, C)
