from sympy import sympify, sin, cos, tan, acos, pi


def evaluate_trig_expression(expression):
    try:
        expr = sympify(expression)
        # Evaluate the expression
        result = expr
        return result
    except Exception as e:
        return str(e)

from sympy import tan, acos

def compute_tan_arccos(value):
    if value < -1 or value > 1:
        return "Invalid input: value must be between -1 and 1"
    arccos_value = acos(value)
    return tan(arccos_value)
