from sympy import asin, pi

def calculate_arcsin(x):
    arcsin_value = asin(x)
    return arcsin_value, arcsin_value.evalf() * (180/pi)

from sympy import sin, cos, tan, pi, sympify

def calculate_trigonometric_expression(expression):
    try:
        expr = sympify(expression)
        return expr.evalf()
    except Exception as e:
        return str(e)
