from sympy import asin, pi

def calculate_arcsin(x):
    arcsin_value = asin(x)
    return arcsin_value, arcsin_value.evalf() * (180/pi)

from sympy import acos, pi

def calculate_angles_of_triangle(a, b, c):
    A = acos((b**2 + c**2 - a**2) / (2 * b * c))
    B = acos((a**2 + c**2 - b**2) / (2 * a * c))
    C = acos((a**2 + b**2 - c**2) / (2 * a * b))
    return (180 * A / pi, 180 * B / pi, 180 * C / pi)

from sympy import sin, cos, tan, sympify

def calculate_trigonometric_expression(expression):
    try:
        return sympify(expression)
    except Exception as e:
        return str(e)

from sympy import tan, acos

def compute_tangent_arccos(value):
    if value < -1 or value > 1:
        return "Invalid input: value must be between -1 and 1"
    arccos_value = acos(value)
    return tan(arccos_value)
