from sympy import Rational

def validate_and_compute_tan_difference(tan_alpha, tan_beta):
    if isinstance(tan_alpha, (int, float)) and isinstance(tan_beta, (int, float)):
        numerator = tan_alpha - tan_beta
        denominator = 1 + (tan_alpha * tan_beta)
        if denominator == 0:
            return "Undefined (division by zero)"
        result = Rational(numerator, denominator)
        return result
    else:
        return "Invalid input types for tangent values."
